export const blogFormatDate = (date) => {
  // const newDate = new Date(date);
  const localFormat = "en-US";
  const options = { month: "long", day: "2-digit", year: "numeric" };
  const formatedDate = Intl.DateTimeFormat("", options).format(new Date(date));

  return formatedDate;
};
