import React, { Component } from "react";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import "../css/blogs.css";

export class Blogs extends Component {
  constructor(props) {
    super(props);

    this.state = {
      error: null,
      isLoaded: false,
      blogs: [],
      visible: 3
    };

    this.loadMore = this.loadMore.bind(this);
  }

  loadMore() {
    this.setState(prev => {
      return { visible: prev.visible + 4 };
    });
  }

  componentDidMount() {
    fetch(
      "https://cors-anywhere.herokuapp.com/https://geeksjapan.asia/blog/api_get_all/"
    )
      .then(res => res.json())
      .then(
        result => {
          this.setState({
            isLoaded: true,
            blogs: result.blogs
          });
        },

        error => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      );
  }

  render() {
    const { error, isLoaded, blogs } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div className="blogs">
          {/* test load more button */}
          <div className="tiles" aria-live="polite">
            {this.state.blogs
              .slice(0, this.state.visible)
              .map((blog, index) => {
                return (
                  <div className="tile fade-in" key={blog.id_blog}>
                    <span className="count">{index + 1}</span>
                    <h2>{blog.title}</h2>
                    <p>{blog.sub_title}</p>
                  </div>
                );
              })}
          </div>
          {this.state.visible < this.state.blogs.length && (
            <button onClick={this.loadMore} type="button" className="load-more">
              Load more
            </button>
          )}

          <Grid container spacing={6}>
            {/* Article Items Start */}
            {blogs.map(blog => (
              <Grid item sm={4}>
                <Card className="card" key={blog.id_blog}>
                  <CardActionArea>
                    <div className="card-thumbnail">
                      <img
                        src={
                          "https://geeksjapan.asia/uploads/blog/thumbnail/" +
                          blog.thumbnail
                        }
                        alt="Thumbnail"
                      />
                    </div>
                    <CardContent>
                      <h5>
                        <i className="fa fa-book"></i> {blog.category}
                      </h5>
                      <span>{blog.sub_title.substring(0, 120) + " ..."}</span>
                    </CardContent>
                  </CardActionArea>
                  <CardActions>
                    <Button color="secondary">Share</Button>
                    <Button color="secondary">Learn More</Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
            {/* Article Items End */}
          </Grid>
        </div>
      );
    }
  }
}

export default Blogs;
