import React, { Component, Fragment } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { getState } from "react-redux";

import Header from "./component/Header";
import Footer from "./component/Footer";
import BlogLists from "./component/BlogLists";
import Read from "./component/Read";
import Login from "./Login";

import { connect } from "react-redux";

const mapStateToProps = state => {
  return {
    counter: state.counter
  };
};

export class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      counter: null
    };
  }

  render() {
    return (
      <Fragment>
        <h1>use redux, counter : {this.props.counter}</h1>
        <Router>
          <Header />
          <Switch>
            <Route exact path="/" component={BlogLists} />

            <Route path="/category/:category" component={BlogLists} />

            <Route path="/read/:title/" component={Read} />

            <Route path="/login" component={Login} />
          </Switch>
          <Footer />
        </Router>
      </Fragment>
    );
  }
}

export default connect(mapStateToProps)(App);
