import React, { Component, Fragment } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { getState } from "react-redux";

import Header from "./component/Header";
import Footer from "./component/Footer";
import BlogLists from "./component/BlogLists";
import Read from "./component/Read";
import Login from "./Login";

import { createStore, applyMiddleware } from "redux";
import { Provider } from "react-redux";
import thunk from "redux-thunk";

import allReducer from "./redux/reducers/";

// const counter = this.state.counter;
//useSelector(state => state.isLogged);
// const mapStateToProps = state => {
//   return {
//     counter: counter;
//   }
//}

const store = createStore(allReducer, applyMiddleware(thunk));

export class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      counter: null
    };

    store.subscribe(() => {
      this.setState({
        counter: store.getState().counter
      });
    });
  }

  render() {
    return (
      <Fragment>
        <h1>use redux, counter : {this.state.counter}</h1>
        <Router>
          <Header />
          <Switch>
            <Route exact path="/" component={BlogLists} />

            <Route path="/category/:category" component={BlogLists} />

            <Route path="/read/:title/" component={Read} />

            <Route path="/login" component={Login} />
          </Switch>
          <Footer />
        </Router>
      </Fragment>
    );
  }
}

export default App;
