import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";

import { getBlogLists } from "../../modules/action/getBlogLists";
import ButtonComponent from "../../component/Button";
import "../../assets/css/blogs.css";
import "../../assets/css/loader.css";

const mapStateToProps = state => ({
  blogs: state.getBlog.blogs,
  isLoaded: state.getBlog.isLoaded
});

const mapDispatchToProps = {
  getBlogLists
};

export class BlogLists extends Component {
  constructor(props) {
    super(props);

    this.state = {
      error: null,
      visible: 6,
      loadMore: false,
      isLoaded: this.props.isLoaded || false,
      blogs: this.props.isLoaded || null
      // categoryName: this.props.match.params.category
    };
  }

  loadMore = () => {
    this.setState(prev => {
      return { visible: prev.visible + 6 };
    });
  };

  fetchData = categoryName => {
    fetch(`http://localhost:9000/blogs/${categoryName ? categoryName : ""}`)
      .then(res => res.json())
      .then(
        result => {
          this.setState({
            isLoaded: true,
            blogs: result.data
          });
        },

        error => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      );
  };

  componentDidMount() {
    let categoryName = this.props.match.params.category;
    this.fetchData(categoryName);
    // this.props.getBlogLists(categoryName);
  }

  shouldComponentUpdate(nextProps) {
    if (nextProps.match.params.category !== this.props.match.params.category) {
      let categoryName = nextProps.match.params.category;

      // this.props.getBlogLists(categoryName);
      // this.fetchData(categoryName);
    }

    return true;
  }

  render() {
    const { error, isLoaded } = this.state;
    let blogs = this.state.blogs;
    console.log(this.state.blogs, this.props.blogs);

    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div className="lds-dual-ring"></div>;
    } else {
      return (
        <div className="blogs">
          <Grid container spacing={6}>
            {/* slice object from api */}
            {blogs.slice(0, this.state.visible).map(blog => (
              // looping blog items start

              <Grid item md={4} sm={12} key={blog.id_blog}>
                <Card className="card">
                  <CardActionArea>
                    <div className="card-thumbnail">
                      <img
                        src={
                          "https://geeksjapan.asia/uploads/blog/thumbnail/" +
                          blog.thumbnail
                        }
                        alt="Thumbnail"
                      />
                    </div>

                    <CardContent>
                      <h5>
                        <i className="fa fa-book"></i> {blog.category}
                      </h5>
                      <p>
                        <b>{blog.title}</b>
                      </p>
                      <span>{blog.sub_title.substring(0, 120) + " ..."}</span>
                    </CardContent>
                  </CardActionArea>

                  <CardActions>
                    <Link to={"../read/" + blog.title.split(" ").join("-")}>
                      Read More
                    </Link>
                  </CardActions>
                </Card>
              </Grid>
              // looping blog items end
            ))}

            {/* button load more start */}
            {this.state.visible < this.state.blogs.length && (
              <div className="load-more">
                <ButtonComponent
                  ButtonName={"Load More"}
                  onClick={this.loadMore}
                />
              </div>
            )}
            {/* button load more end */}
          </Grid>
        </div>
      );
    }
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(BlogLists);
