export const fetchBlog = categoryName => {
  const URL = "http://localhost:9000/blogs/";
  const category = categoryName || "";

  fetch(URL + categoryName)
    .then(res => res.json())
    .then(result => {
      return {
        isLoaded: true,
        blogs: result.data
      };
    })
    .catch(error => {
      this.setState({
        isLoaded: true,
        error
      });
    });
};
