import React, { Component } from "react";
import Button from "@material-ui/core/Button";

import { connect } from "react-redux";

import { loginAction } from "./redux/action/loginAction";
import "./css/login.css";

const mapStateToProps = state => {
  const { loggingIn } = state.authentication;
  return { loggingIn, email };
  // return {
  //   loggingIn: state.authentication
  // };
};

const mapDispatchToProps = {
  login: loginAction.login
};

export class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      submitted: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  };

  handleSubmit = event => {
    event.preventDefault();

    this.setState({ submitted: true });
    const { email, password } = this.state;
    if (email && password) {
      this.props.login(email, password);
    }
  };

  render() {
    const loggingIn = this.props.loggingIn;
    const { submitted, email, password } = this.state;

    return (
      <div className="section">
        <h2>Login Page {loggingIn ? "True" : "False"}</h2>
        <h2>is Login {this.props.isLogin ? "true" : "false"}</h2>
        <form onSubmit={this.handleSubmit}>
          <input
            type="text"
            name="email"
            id="email"
            placeholder="email"
            onChange={this.handleChange}
          />
          {submitted && !email && <p>Email is required</p>}
          <input
            type="password"
            name="password"
            id="password"
            placeholder="password"
            onChange={this.handleChange}
          />
          {submitted && !password && <p>Password is required</p>}
          <Button color="secondary" variant="contained" type="submit">
            Submit
          </Button>
        </form>
      </div>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
