const initialState = {
  className: "",
  message: null
};

export const alert = (state = initialState, action) => {
  switch (action.type) {
    case "ALERT_SUCCESS":
      return {
        className: "alert-success",
        message: action.message
      };
    case "ALERT_ERROR":
      return {
        className: "alert-danger",
        message: action.message
      };
    case "ALERT_CLEAR":
      return {};
    default:
      return state;
  }
};
