import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import logoutAction from "../redux/action/logoutAction";
import "../css/header.css";

const mapStateToProps = state => {
  const { loggedIn } = state.authentication;
  return { loggedIn };
};

const mapDispatchToProps = {
  logout: logoutAction
};

export class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      login: this.props.login,
      logout: this.props.logout
    };
  }

  logout = event => {
    this.props.logout;
  };

  render() {
    const loggedIn = this.props.loggedIn;
    return (
      <div className="header">
        <Link to="/">
          <img
            src="https://geeksjapan.asia/assets/img/logo.png"
            alt="Logo"
            width="120px"
          />
        </Link>
        <Link to="/category/life">Life</Link>
        <Link to="/category/explore">Explore</Link>
        <Link to="/category/work">Work</Link>
        <Link to="/category/culture">Culture</Link>
        {loggedIn ? (
          <Link to="/login"> | Login | </Link>
        ) : (
          <Button onClick={logout}> | Logout | </Button>
        )}
      </div>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Header);
