export const blogFormatDate = (date) => {
  const localDate = "en-US";

  const options = {
    month: "long",
    day: "2-digit",
    year: "numeric",
  };

  const formatedDate = new Date(localDate).toLocaleDateString([], options);

  return formatedDate;
};
