let initialState = {
  isLoaded: false,
  error: null,
  blogs: null
};

export const getBlog = (state = initialState, action) => {
  switch (action.type) {
    case "BLOG_LISTS":
      return {
        isLoaded: true,
        blogs: action.blogs
      };
    case "READ_BLOG":
      return {
        blogs: action.blogs
      };
    case "FETCH_ERROR":
      return {
        blogs: null,
        error: action.error
      };
    default:
      return state;
  }
};
