export const blogFormatDate = (getDate) => {
  // const newDate = new Date(date);
  const localFormat = "en-US";
  const options = { month: "long", day: "2-digit", year: "numeric" };

  // return new Intl.DateTimeFormat(localFormat, options).format(
  //   new Date(getDate.toString())
  return new Date(getDate).toLocaleDateString([], options);
};
