import React, { Component } from "react";

export class Read extends Component {
  constructor(props) {
    super(props);

    this.state = {
      error: null,
      isLoaded: false,
      visible: 6,
      loadMore: false
    };

    this.loadMore = this.loadMore.bind(this);
  }

  loadMore() {
    this.setState(prev => {
      return { visible: prev.visible + 3 };
    });
  }

  componentDidMount() {
    // get blogs content from api
    fetch("http://localhost:9000/blogs/")
      .then(res => res.json())
      .then(
        result => {
          this.setState({
            isLoaded: true,
            blogs: result.data
          });
        },

        error => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      );
  }

  render() {
    return (
      <div>
        <h1>Read More</h1>
      </div>
    );
  }
}

export default Read;
