import React, { Component } from "react";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import "../css/blog.css";

export class Blogs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    fetch(
      "https://cors-anywhere.herokuapp.com/https://geeksjapan.asia/blog/api_get/"
    )
      .then(res => res.json())
      .then(
        result => {
          this.setState({
            isLoaded: true,
            items: result.blogs
          });
        },

        error => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      );
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div className="blogs">
          <ul>
            {items.map(item => (
              <li key={item.id_blog}>
                {item.title} {item.category}
              </li>
            ))}
          </ul>
          <Grid container spacing={6}>
            {/* Article Items Start */}
            <Grid item sm={4}>
              <Card className="card">
                <CardActionArea>
                  <img
                    src="https://geeksjapan.asia/uploads/blog/thumbnail/abe_and_putin.jpg"
                    alt="Thumbnail"
                  />
                  <CardContent>
                    <h5>
                      <i className="fa fa-book"></i> Lizard
                    </h5>
                    <span>
                      Lizards are a widespread group of squamate reptiles, with
                      over 6,000 species, ranging across all continents except
                      Antarctica
                    </span>
                  </CardContent>
                </CardActionArea>
                <CardActions>
                  <Button color="secondary">Share</Button>
                  <Button color="secondary">Learn More</Button>
                </CardActions>
              </Card>
            </Grid>
            {/* Article Items End */}
          </Grid>
        </div>
      );
    }
  }
}

export default Blog;
