export const blogFormatDate = (date) => {
  // const newDate = new Date(date);
  const options = { month: "long", day: "2-digit", year: "numeric" };
  const formatedDate = Intl.DateTimeFormat("en-US", options).format(
    new Date(date)
  );

  return formatedDate;
};
