import React, { Component } from "react";
import { Router, Route, Switch } from "react-router-dom";
import { connect } from "react-redux";

import history from "../helper/history";
import PrivateRoute from "./PrivateRoute";
import { alertClear } from "../modules/action/alert";

import Header from "../component/Header";
import Footer from "../component/Footer";
import BlogLists from "./Blog/BlogLists";
import Read from "./Blog/Read";
import Login from "./Login";
import Register from "./Register";
import Dashboard from "./Dashboard";

export class Routes extends Component {
  constructor(props) {
    super(props);

    history.listen((location, action) => {
      // clear alert on location change
      // this.props.clearAlert();
    });
  }
  render() {
    const { alertMessage, alertClassName } = this.props;
    console.log(alertClassName);
    return (
      <div>
        <div className="">dsadasd</div>
        {alertMessage && <div className={alertClassName}>{alertMessage}</div>}
        <Router history={history}>
          <Header />
          <Switch>
            <PrivateRoute path="/dashboard" component={Dashboard} />
            <Route exact path="/" component={BlogLists} />
            <Route path="/category/:category" component={BlogLists} />
            <Route path="/read/:title/" component={Read} />
            <Route path="/login" component={Login} />
            <Route path="/register" component={Register} />
            <Route from="*" to="/" />
          </Switch>
          <Footer />
        </Router>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  alertMessage: state.alert.message,
  alertClassName: state.alert.className
});

const mapDispatchToProps = {
  clearAlert: alertClear
};

export default connect(mapStateToProps, mapDispatchToProps)(Routes);
