export const getBlogLists = categoryName => {
  const URL = "http://localhost:9000/blogs/";
  const category = categoryName || "";

  return dispatch => {
    fetch(URL + category)
      .then(res => res.json())
      .then(result => {
        dispatch({
          type: "BLOG_LISTS",
          isLoaded: true,
          blogs: result.data
        });
      })
      .catch(error => {
        dispatch({
          type: "READ_BLOG",
          isLoaded: true,
          error
        });
      });
  };
};
