import React, { Component } from "react";
import "../css/read.css";

export class Read extends Component {
  constructor(props) {
    super(props);

    this.state = {
      error: null,
      isLoaded: false
    };
  }

  componentDidMount() {
    const title = this.props.match.params.title;
    // get blogs content from api
    fetch(`http://localhost:9000/blog/read/${title}`)
      .then(res => res.json())
      .then(
        result => {
          this.setState({
            isLoaded: true,
            blogs: result.data
          });
        },

        error => {
          this.setState({
            isLoaded: false,
            error
          });
        }
      );
  }

  render() {
    const { error, isLoaded, blogs } = this.state;

    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div className="lds-dual-ring"></div>;
    } else {
      return (
        <div className="blog-section">
          {blogs.map(blog => (
            <div>
              <img
                className="thumbnail"
                src={
                  "http://localhost/geeksjapan/uploads/blog/thumbnail/" +
                  blog.thumbnail
                }
              />
              <div className="content">
                <p className="category">{blog.category}</p>
                <p className="title">{blog.title}</p>
                <p className="sub_title">{blog.sub_title}</p>
                <span className="author">GeeksJapan</span>
                <span className="date">
                  {/* {Date(blog.date).toLocaleDateString("en-US")} */}
                  {new Intl.DateTimeFormat("MM/YYYY/DD").format(
                    new Date(blog.date)
                  )}
                </span>
              </div>
            </div>
          ))}
        </div>
      );
    }
  }
}

export default Read;
