import React, { Component } from "react";

export class Read extends Component {
  constructor(props) {
    super(props);

    this.state = {
      error: null,
      isLoaded: false
    };
  }

  componentDidMount() {
    const { id_blog } = this.props.match.params;
    // get blogs content from api
    fetch("http://localhost:9000/blog/read/" + get_id_blog)
      .then(res => res.json())
      .then(
        result => {
          this.setState({
            isLoaded: true,
            blogs: result.data
          });
        },

        error => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      );
  }

  render() {
    return (
      <div>
        <h1>Read More {this.state.visible}</h1>
      </div>
    );
  }
}

export default Read;
